# make crabs kable style
make_crab_as_kable <- function(x, y){
  crab_gtkbl_df <- df |> 
    select(group, all_of(x), all_of(y)) |>
    dplyr::mutate(group = forcats::fct_rev(group)) |> 
    gtsummary::tbl_strata(
    strata = group,
    ~ .x |>
      gtsummary::tbl_cross(
        row = !!sym(x),
        col = !!sym(y),
        percent = "row",
        statistic = "{p}%",
        margin = "row",
        missing = "no"
      )
  ) |> 
    gtsummary::bold_labels() |> 
    gtsummary::as_kable_extra(
      booktabs = T,
      longtable = T,
      linesep = ""
    ) |> 
    kableExtra::kable_styling(
      position = "left",
      bootstrap_options = c("striped", "hover", "responsive")
    )
}

# make crabs gt style
make_crab_gtsummary <- function(x, y){
  crab_gt_df <- df |> select(group, all_of(x), all_of(y)) |>
    dplyr::mutate(group = forcats::fct_rev(group)) |> 
    gtsummary::tbl_strata(
    strata = group,
    ~ .x |>
      gtsummary::tbl_cross(
        row = !!sym(x),
        col = !!sym(y),
        percent = "row",
        statistic = "{p}%",
        margin = "row",
        missing = "no"
      )
  ) |> 
    gtsummary::bold_labels() |> 
    gtsummary::as_gt() |> 
    gt::tab_footnote("NA omitted. Table reflects row percentages") |>
    gt::tab_options(table.font.size = "small",
                    data_row.padding = gt::px(1))
}
```
